dunes-script-module.script:
usergroups = container.userGroups;
usergroupName = usergroupName.toLowerCase();
var groups = [];
for (var index in usergroups) {
	var groupName = usergroups[index].name.toLowerCase();
	if(groupName.search(usergroupName) > -1) {
		groups.push(usergroups[index]);
	}
}
return groups;
