dunes-script-module.script:
// Get NICs from Edge
var NICs = edge.nics;
var arrayNICs = NICs.nics;
System.log("Found "+arrayNICs.length+" NICs on the '"+edge.name+"' edge.");

// now get names of the NICs and retun in an array
var nicNames = [];
for(n in arrayNICs) {
	var name = arrayNICs[n].name;
	nicNames.push(name);
}
return nicNames;
