dunes-script-module.script:
var policies = [];

if (location) {
	if (location == "frk") {
		policies = ["Replicated - Franklin Primary", "Non-replicated - Franklin Only"];
	} else {
		policies = ["Replicated - Milwaukee Primary", "Non-replicated - Milwaukee Only"];
	}
} else {
	policies = ["Please select the location above."];
}

return policies;
