dunes-script-module.script:
// Define Environment Array
var environment = [];

if (location) {
	// Set Array order for environment values
	if(location == "frk") {
		environment = ["Production", "Non-Production", "Development"];
	} else {
		environment = ["Non-Production", "Production", "Development"];
	}

} else {
environment.push("Please select a datacenter location above.")
}

return environment;
