dunes-script-module.script:
var retArray = [];
if (tenant !== null && tenant !== undefined && tenant !== "") {
	try {
		var items = sovlabsNamingManager.getAll();

		for each (var item in items) {
			if (item.tenant == tenant) {
				retArray.push(item.shortName);
			}
		}
	} catch (e) {

	}
}

return retArray;
