dunes-script-module.script:
var retArray = [];

try {
	var items = sovlabsRestipeconfigManager.getAllbyTenant(tenant);

	if (type.toLowerCase() == "") {
		for each (var item in items) {
			retArray.push(item.shortName);
		}
	} else {
		for each (var item in items) {
			if (item.type == type) {
				retArray.push(item.shortName);
			}
		}
	}
} catch (e) {
	//Unable to retrieve Restipe by tenant
}

return retArray;
