dunes-script-module.script:
var levels = path.split("/");
var index = 0;
var categories = Server.getAllResourceElementCategories();

return getResourceElementCategory(levels, index, categories);

function getResourceElementCategory(levels, index, categories) {
	if (index < levels.length) {
		for each (var cat in categories) {
			if (cat.name == levels[index]) {
				index++;
				if (index < levels.length) {
					return getResourceElementCategory(levels, index, cat.subCategories);
				} else {
					if (cat.path == levels.join("/")) {
						return cat;
					}
				}
			}
		}
	}
}

return null;
