workflow-item.script:
//Pull the Reference ID out of the response content

//Example contentAsString = "record:host/ZG5zLmhvc3QkLl9kZWZhdWx0LmNvbS51c3Njby5kcy5uYS5jaDJscHRlc3QwMTM:ch2lptest013.na.ds.ussco.com/default"
var contentAsString = requestResponse.contentAsString;

//Chop off the text before the Ref Id (up to the slash)
if (contentAsString.indexOf("/") == -1) {
	throw "Invalid content, does not contain a valid Reference Id: '" + contentAsString + "'.";
}
var afterSlash = contentAsString.split("host/")[1];

//Chop off the text after the Ref Id (starting with the next colon)
if (afterSlash.indexOf(":") == -1) {
	throw "Invalid content, does not contain a valid Reference Id: '" + contentAsString + "'.";
}
hostReferenceId = afterSlash.split(":")[0];

//Log the Reference ID
System.log("Host record has Reference ID '" + hostReferenceId + "'.");

var statusCode = requestResponse.statusCode;

if (statusCode != 200) { 
	throw "REST operation did not produce the expected status code (200).";
}
