workflow-item.script:
//Create a dynamic type ID for the VM (required by the attach() method)
System.log("Associating tag ID '" + tagId + "' with VM '" + vcVm.name + "'...");
var client = vapiEndpoint.client();
var tagging = new com_vmware_cis_tagging_tag__association(client);
var dynamicId = new com_vmware_vapi_std_dynamic__ID() ;
dynamicId.id = vcVm.id;
dynamicId.type = vcVm.vimType;
System.debug(dynamicId);

//Call the attach() method and look for a common error
try {
	tagging.attach(tagId, dynamicId);
} catch (err) {
	if (err.message.indexOf("com.vmware.vapi.std.errors.invalid_argument") != -1) {
		err.message += " NOTE: one of the causes of this error is trying to assign "
					+  "a single-cardinality tag, when there is already one assigned.";
	}
	throw err;
}

System.log("Tag association was successful.");

if (tagId == null) {
  throw "'tagId' parameter should not be null";
}
if (vcVm == null) {
  throw "'vcVm' parameter should not be null";
}
