workflow-item.script:
if (endpoint == null) {
  throw "'endpoint' parameter should not be null";
}

var client = endpoint.client();
var tagging = new com_vmware_cis_tagging_category(client);
var list = tagging.list();

//[{associable_types=[], name=test-ted-cat, description=, id=urn:vmomi:InventoryServiceCategory:e6549f2d-b23b-4608-9ec3-c17516e820ed:GLOBAL, used_by=[], cardinality=SINGLE}]
System.log("Note: the used_by field does NOT indicate which tags are associated with a category. It seems to always be blank.");
for each (var catId in list){
	var catObject = tagging.get(catId);
	System.log(
		"Name:" + catObject.name + ", " +
		"Types:" + catObject.associable_types + ", " +
		"Cardinality:" + catObject.cardinality + ", " +
		"ID:" + catObject.id + ", " +
		"Used by:" + catObject.used_by
	);
}
