workflow-item.script:
//Update or add any Custom Properties here
// Event Broker Workflow Subscription must be set to Blocking.  To change an existing 
// subscription from non-Blocking to Blocking, delete it and re-create it.

//Preserve any custom property additions from previous workflows in this LifeCycle state
var newProps = payload.get("virtualMachineAddOrUpdateProperties");
if (!newProps) {
	newProps = new Properties();
}

//Example custom property addition:
//newProps.put('VirtualMachine.Network0.Name', 'dvPortGroup');
newProps.put('CloneSpec', custSpec);
newProps.put('ucp.domain', domainName);
newProps.put('VirtualMachine.Disk0.StorageReservationPolicy', storageResPolicy);
newProps.put('VirtualMachine.Network0.PrimaryDns', primaryDns);
newProps.put('VirtualMachine.Network0.SecondaryDns', secondaryDns);
newProps.put('VirtualMachine.Network0.DnsSuffix', dnsSuffix);
newProps.put('VirtualMachine.Network0.DnsSearchSuffix', dnsSearchSuffixes);



virtualMachineAddOrUpdateProperties = newProps;

// 1. Define Variables
var environment = customProperties.get("ucp.tag.environment");
var os = customProperties.get("ucp.os");
var location = customProperties.get("ucp.tag.location");
var storageResPolicy = customProperties.get("VirtualMachine.Disk0.StorageReservationPolicy");

// 2. Define default selection of Storage Reservation Policy if the field was not selected in form.
if(!storageResPolicy) {
	if(location == "frk") {
		storageResPolicy = "Replicated - Franklin Primary";
	} else {
		storageResPolicy = "Replicated - Milwaukee Primary";
	}
}



// 3. For Windows Machines
// Windows 2016 Deployments
if(os.search("Windows 2016") > -1) {
	switch(environment) {
		case "Production":
			custSpec = "Server_2016_nm";
			adOU = prodWinOU;
			domainName = "nm.nmfco.com";
			break;
		case "Non-Production":
			custSpec = "Server_2016_nmtest";
			adOU = NonProdWinOU;
			domainName = "nmtest.nmfco.com";
			break;
		case "Development":
			custSpec = "Server_2016_nmdev";
			adOU = devWinOU;
			domainName = "nmdev.nmfco.com";
	}
// Windows 2012 Deployments
} else if(os.search("Windows 2012") > -1) {
	switch(environment) {
		case "Production":
			custSpec = "Server_2016_nm";
			adOU = prodWinOU;
			domainName = "nm.nmfco.com";
			break;
		case "Non-Production":
			custSpec = "Server_2016_nmtest";
			adOU = NonProdWinOU;
			domainName = "nmtest.nmfco.com";
			break;
		case "Development":
			custSpec = "Server_2016_nmdev";
			adOU = devWinOU;
			domainName = "nmdev.nmfco.com";
	}
// Linux Deployments
// For reasons until we have non-posix support all machines need to go to NM
} else {
	switch(environment) {
		case "Production":
			custSpec = "Enterprise Linux 7 - nm.nmfco.com";
			adOU = prodNixOU;
			domainName = "nm.nmfco.com";
			break;
		case "Non-Production":
			//custSpec = "Enterprise Linux 7 - nmtest.nmfco.com";
			custSpec = "Enterprise Linux 7 - nm.nmfco.com";
//			adOU = NonProdNixOU;
			adOU = prodNixOU;
			domainName = "nm.nmfco.com";;
			break;
		case "Development":
//			custSpec = "Enterprise Linux 7 - nmdev.nmfco.com";
			custSpec = "Enterprise Linux 7 - nm.nmfco.com";
			//adOU = devNixOU;
			adOU = prodNixOU;
			domainName = "nm.nmfco.com";
	}
}

// Set DNS servers based on location
var location = customProperties.get("ucp.tag.location");
var primaryDns;
var secondaryDns;

if(location == "frk") {
	primaryDns = "172.16.31.41";
	secondaryDns = "172.16.29.41";
} else { 
	primaryDns = "172.16.29.41";
	secondaryDns = "172.16.31.41";
}

var dnsSuffix = domainName;
var dnsSearchSuffixes = "nml.com,nm.nmfco.com,nmtest.nmfco.com,nmdev.nmfco.com";

// Set variables for Infoblox Host Record Creation
var hostName = vmName+"."+domainName;
var ipAddress = customProperties.get("VirtualMachine.Network0.Address");

// Set a single alias
var alias = customProperties.get("ucp.alias");
alias = alias+"."+domainName;
//var alias = vmName+".nml.com";
var aliases = [alias];
