workflow-item.script:
//We take all of the variables and work above and generate our etcd keys and values arrays.
var baseURL = 'unifiedcompute/servers/';  //the base URL for all UCP records in etcd
var serverURL = baseURL + serverName + '/';
var sudoSpecURL = serverURL + 'sudo/sudoSpecs/' + specName + '/';
var safesudoer = sudoer.replace(/_/i,'');
var etcd_keys = [];
var etcd_values = [];

//First set the state of the server needsDay2
etcd_keys.push(serverURL + 'state');
etcd_values.push('needsDay2');

//brute Force
 var d = new Date();

etcd_keys.push(sudoSpecURL + 'addedOn');
etcd_values.push(System.formatDate(d,'yyyMMdd-HHmm'));

etcd_keys.push(sudoSpecURL + 'state');
etcd_values.push('grant');

etcd_keys.push(sudoSpecURL + 'name');
etcd_values.push(specName);


adHostString = "adProd";
	var adElement = [];
		var ConfigElCategory = Server.getConfigurationElementCategoryWithPath("UCP");
		System.log(ConfigElCategory.name);
		var configEls = ConfigElCategory.allConfigurationElements;
		for(element in configEls) {
			if(configEls[element].name == "ActiveDirectory") {
			adElement = configEls[element];
			System.log("AD Elements: "+adElement.name);
			}
		}

	// 2.1.2 Get AD Host
		var adHost;
		if(adElement.getAttributeWithKey(adHostString) != null) {
			attribute = adElement.getAttributeWithKey(adHostString);
			adHost = attribute.value;
			System.log(adHost.name);
		}
	//we need to identify if this is a user or group so that sudo is happy

	var groupSearch = ActiveDirectory.search("UserGroup",sudoer,adHost);
	System.log('Group search: ' + groupSearch);
	if (groupSearch.length > 0)
	{
		etcd_keys.push(sudoSpecURL + 'users/'+ safesudoer +'/linuxName');
		etcd_values.push(encodeURI('%'+ sudoer));  //Lookup linuxname if a group
	}
	else
	{
		etcd_keys.push(sudoSpecURL + 'users/'+ safesudoer +'/linuxName');
		etcd_values.push(sudoer);  //Lookup linuxname if a group
	}

etcd_keys.push(sudoSpecURL + 'users/'+ safesudoer +'/state');
etcd_values.push(state);  

//Finally we define the users who we are manipulating
etcd_keys.push(sudoSpecURL + 'users/'+ safesudoer +'/name');
etcd_values.push(sudoer);



if (state == 'grant')
{
etcd_keys.push(sudoSpecURL + 'users/'+ safesudoer + '/addedOn');
etcd_values.push(System.formatDate(d,'yyyMMdd-HHmm'));

etcd_keys.push(sudoSpecURL + 'users/'+ safesudoer + '/addedBy');
etcd_values.push(requestor)
}

else if (state == 'revoke')
{
etcd_keys.push(sudoSpecURL + 'users/'+ safesudoer + '/revokedOn');
etcd_values.push(System.formatDate(d,'yyyMMdd-HHmm'));

etcd_keys.push(sudoSpecURL + 'users/'+ safesudoer + '/revokedBy');
etcd_values.push(requestor)
}
