workflow-item.script:
var vms = [];
var vcVm;
var bgCustomProp = "nm.ucp.BusinessGroupName";
var envCustomProp = "ucp.tag.environment";
var pdlCustomProp = "nm.ucp.businessGroupPDL";

var b = "Business Group";
var e = "Environment";
var p = "Business Group PDL";

//Getting sdk connections
var sdkConnections = VcPlugin.allSdkConnections;
for(var i in sdkConnections){
	   var hostvc = sdkConnections[i];
}

var vms = Server.findAllForType("vCAC:VirtualMachine");
vms.sort();

for each (var vm in vms) {
			if (vm.isManaged == true && vm.virtualMachineName!= "ucpadmin01263"){
				try {
					//Get Custom Properties from vm
					var customProp = System.getModule("com.vmware.library.vcac").getPropertiesFromVirtualMachine(host,vm) ;

					//Setting Variables for properties
					var env = customProp.get(envCustomProp);
					var bg = customProp.get(bgCustomProp);
					var pdl = customProp.get(pdlCustomProp);

					//Documenting properties
					System.log("VM name: "+vm.virtualMachineName);
					System.log("VM is Template: "+vm.isTemplate);
					System.log("VM is Managed: "+vm.isManaged);
					System.log("Business Group = "+bg);
					System.log("Environment = "+env);
					System.log("PDL = "+pdl);

					//Convert vcac:virtualmachine to vc:virtualmachine
	    		   	var vSphereVms = hostvc.getAllVirtualMachines(null, "xpath:contains(name, '" + vm.getEntity().getProperty("VirtualMachineName") + "')");
				   	vSphereVm = vSphereVms[0];

					//Setting Custom Attributes on vm within vcenter
					if (env != null){
						System.getModule("com.vmware.library.vc.customattribute").setOrCreateCustomField(vSphereVm,e,env);
					} else if (env == null){
						System.error("Environment is null for "+vSphereVm.name);
					}

					if (bg != null){
						System.getModule("com.vmware.library.vc.customattribute").setOrCreateCustomField(vSphereVm,b,bg);
					} else if (bg == null){
						System.error("Business Group is null for "+vSphereVm.name);
					}

					if (pdl != null){
						System.getModule("com.vmware.library.vc.customattribute").setOrCreateCustomField(vSphereVm,p,pdl);
					} else if (pdl == null){
						System.error("PDL is null for "+vSphereVm.name);
					}

					System.log("\n");
				}

			catch(err){
				System.error("Error with "+vSphereVm.name+", message = "+err)
			}
		}
}
