workflow-item.script:
System.log("Servers Selected = "+servers);
var vcacVm;
var vcVM;
//Initialize Arrays
var vcacServerOut = new Array();
var vcServerOut = new Array();

//Find VC:VirtualMachine and vCAC:VirtualMachine for each server inputted
for each (server in servers){
	System.log("Servername = "+server);
	vcacVm = Server.findAllForType("vCAC:VirtualMachine", "VirtualMachineName eq '" +server+"'");
	vcVM = Server.findAllForType("VC:VirtualMachine","xpath:name='"+server+"'");  
	//Output them into an Array
	vcServerOut.push(vcVM[0]);
	vcacServerOut.push(vcacVm[0]);
}

System.log(vcacServerOut);
System.log(vcServerOut);
