workflow-item.script:
var host = Server.findAllForType("vCAC:VCACHost")[0]; //find vcac host
var vms = Server.findAllForType("vCAC:VirtualMachine");//find all vcac virtual machines
var module = System.getModule("com.vmware.library.vcac")//pulling getmodule out of the loop for performance
//var custProp = "nm.ucp.BusinessGroupName";

//Sorting vms
vms.sort();

for each (vm in vms) {
	if (vm.isManaged == true) {

		//Getting Properties of vm
		var props = module.getPropertyFromVirtualMachine(host,vm,custProp);

		if (props == null || props == ""){
		var VMName = vm.virtualMachineName;
		System.log("VMName = "+VMName+" / "+props);
		}
	}
}
