workflow-item.script:
var bgProp = "nm.ucp.BusinessGroupName";
var vcacHost = Server.findAllForType("vCAC:VCACHost")[1];
var bgs = [] ;
var contactPDL = "nm.ucp.businessGroupPDL";

//Get all Business Groups
var bgs = vCACCAFEEntitiesFinder.findSubtenants(host);

//Loop through all business groups and get vcac virtual machine
for each (bg in bgs){
	//var cherwellValue = vCACCAFESubtenantHelper.getCustomPropertyValue(bg,cherwellName);
	//System.log("Business Group = "+bg.name);
	//System.log("cherwellValue = "+cherwellValue);

	// Get vCAC:VirtualMachine for each server
	var filter = "VirtualMachineProperties/any(p: p/PropertyName eq '" + bgProp + "' and p/PropertyValue eq '" + bg.name + "')";
 	var machines = vCACEntityManager.readModelEntitiesBySystemQuery(
    vcacHost.id,                        //vCAC IaaS host id
    "ManagementModelEntities.svc",  //model
    "VirtualMachines",              //Entity Type in IaaS API
    filter                          //filter
    )
 	//converting vcac:entity to vcac:virtualmachine
	var vCACVirtualMachines = machines.map(function(machine) {
    return machine.getInventoryObject();
	});
	vCACVirtualMachines.sort();
		//for each server in business group update custom property
		for each(var vm in vCACVirtualMachines){
			//if (bg.name == "Automation"){
			var pdl = vCACCAFESubtenantHelper.getCustomPropertyValue(bg,contactPDL);
			System.log("Business Group = "+bg.name);
			//System.log("BG PDL = "+pdl);
			//System.log("ServerName = "+vm.VirtualMachineName);
			var currentPdl = System.getModule("com.vmware.library.vcac").getPropertyFromVirtualMachine(vcacHost,vm,contactPDL)
			if (currentPdl == null){
				System.log("Contact PDL is null for "+vm.VirtualMachineName);
				//System.getModule("com.nm.ucp").addUpdatePropertyFromVirtualMachineEntity(vcacHost, contactPDL, pdl, propertyIsHidden, propertyIsRuntime, propertyIsEncrypted, doNotUpdate, vm) ;
			}
			System.log(vm.VirtualMachineName+" currentValue = "+currentPdl);
			//}
		}
}
