workflow-item.script:
/*Updates Custom Properties for Business Groups.  Can filter to run against one or comment out to run against all Business Groups
***This will only work if the virtual machine has the tag nm.ucp.BusinessGroupName set correctly.***
*/

//Defining Variables
var bgName = "nm.ucp.BusinessGroupName";
var bgPdl = "nm.ucp.businessGroupPDL";
var bgCherwell = "nm.ucp.cherwellName";
var vcacHost = Server.findAllForType("vCAC:VCACHost")[1];
var bgs = [];
var props = [];
var custPropPdl;
var custPropCher;

//Get all Business Groups
bgs = vCACCAFEEntitiesFinder.findBusinessGroups(host);
bgs.sort();

for each (var bg in bgs){
	System.log("Business Group = "+bg.name);
	//Getting Properties for Business Group
	props = vCACCAFEEntitiesFinder.getCustomProperties(bg);
	for each (var prop in props){
		if (prop.name == bgPdl){custPropPdl = prop.value}; //Setting Group PDL
		if (prop.name == bgCherwell){custPropCher = prop.value}; //Setting Group PDL
	}
		System.log("nm.ucp.businessGroupPDL Value = "+custPropPdl);
		System.log("nm.ucp.cherwellName Value = "+custPropCher);
	//Getting all Virtual Machines for the Business Group that are tagged with matching nm.ucp.BusinessGroupName property
	var filter = "VirtualMachineProperties/any(p: p/PropertyName eq '" + bgName + "' and p/PropertyValue eq '" + bg.name + "')";
 	var machines = vCACEntityManager.readModelEntitiesBySystemQuery(
    vcacHost.id,                        //vCAC IaaS host id
    "ManagementModelEntities.svc",  //model
    "VirtualMachines",              //Entity Type in IaaS API
    filter                          //filter
    )
 	//converting vcac:entity to vcac:virtualmachine
	var vCACVirtualMachines = machines.map(function(machine) {
    return machine.getInventoryObject();
	});

	//Sorting vms
	vCACVirtualMachines.sort();

	for each(var vm in vCACVirtualMachines){
	//Run for a specific business group
	if (bg.name == "Business Objects"){ //Comment this line and end of if to run against all
	//Get PDL Custom Attribute and if not match BG, Set it
	var pdlValue = System.getModule("com.vmware.library.vcac").getPropertyFromVirtualMachine(vcacHost,vm,bgPdl)
		if (pdlValue == null || pdlValue == "" || pdlValue !== pdlValue){
			System.getModule("com.nm.ucp").addUpdatePropertyFromVirtualMachineEntity(vcacHost, bgPdl, custPropPdl, propertyIsHidden, propertyIsRuntime, propertyIsEncrypted, doNotUpdate, vm) ;
		}

	//Get Cherwell Name Custom Attribute and if not match BG, Set it
	var cherValue = System.getModule("com.vmware.library.vcac").getPropertyFromVirtualMachine(vcacHost,vm,bgCherwell)
		if (cherValue == null || cherValue == "" || cherValue !== custPropCher){
			//System.getModule("com.nm.ucp").addUpdatePropertyFromVirtualMachineEntity(vcacHost, bgCherwell, custPropCher, propertyIsHidden, propertyIsRuntime, propertyIsEncrypted, doNotUpdate, vm) ;
		}
	System.log(vm.VirtualMachineName+" pdlValue = "+pdlValue);
	System.log(vm.VirtualMachineName+" bgCherwell = "+cherValue);
	System.log("____");
			}//comment this line out and if above to run against all
		}		
	System.log("------------------------------------");
}
