workflow-item.script:
var cafeHost = Server.findAllForType("vCACCAFE:VCACHost")[0];
var endpoint = "com.vmware.vcac.core.cafe.reservation.api";
var resourceUrl = "reservations?limit=1000";
var bg = "bktest 72"
// Rest Client

var restClient = cafeHost.createRestClient(endpoint);

// Execute
var response = restClient.get(resourceUrl)
//System.log("response = "+response);

// Get Output
var body = response.getBodyAsJson()
System.log(body);

var reservations = body['content'];
System.log(reservations);

for (var i = 0; i < reservations.length; i++) {
System.log(reservations[i].name);
}
