workflow-item.script:
var multilines = "===Summary===\nCampus: MKE\nProject Name(s): vro-test4_nonprod\nJump Server Floating IP: 10.41.16.47";

var lines = new Array();
lines = multilines.toString().split("\n");

for(var i = 0;i < lines.length;i++){
    //code here using lines[i] which will give you each line
	//System.log(lines[i])
	if (!lines[i].indexOf("Jump Server Floating IP")) {
		var floatingIP = lines[i].split(":")[1]
	}
	if (!lines[i].indexOf("Project Name(s)")) {
		var projectName = lines[i].split(":")[1]
	}
}

System.log("===Output===");
System.log("	Project Name: " + projectName);
System.log("	Floating IP: " + floatingIP);
